let city_details = document.querySelector("#city_details");

function search() {
    let city = document.querySelector("#city");
    let x = city.value;
    let url = `https://api.openweathermap.org/data/2.5/weather?q=${x}&appid=a1e374b0d011589101b5ea37e87c9663`
    fetch(url).then(function (res) {
        return res.json();
    }).then(function (res) {
        weather_details(res);
    })
    city.value = null;
    city_details.style.display = "flex";
}
let arr = ["Sunny", "Cold", "Too hot"];
let arr1 = [
    "data:image/png;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,"
]
let weather = document.querySelector("#city_details");
let wind = document.querySelector("#wind");
let maxt = document.querySelector("#maxt");
let mint = document.querySelector("#mint");
let hum = document.querySelector("#hum");
let x=document.querySelector("#next_five_days");
function weather_details(res) {
    weather.innerHTML = null;
    let temp = document.createElement("h1");
    temp.innerText = `${(Number(res.main.temp)-273).toFixed(1)}°C`;
    let div1 = document.createElement("div");
    div1.append(temp);
    let div2 = document.createElement("div");
    let city_name = document.createElement("h2");
    city_name.innerText = res.name;
    console.log(res.name);
    div2.append(city_name);
    let div3 = document.createElement("div");
    let images = document.createElement("img");
    let paragraph = document.createElement("p");
    if ((Number(res.main.temp) - 273) <= 20) {
        paragraph.innerText = arr[1];
        images.src = [arr1[1]];
    } else if ((Number(res.main.temp) - 273) > 20 && (Number(res.main.temp) - 273) <= 32) {
        paragraph.innerText = arr[0];
        images.src = arr1[0];
    } else {
        paragraph.innerText = arr[2];
        images.src = arr1[2];
    }
    let map = document.querySelector(".gmap_canvas>iframe");
    map.src = `https://maps.google.com/maps?q=${res.name}&t=&z=13&ie=UTF8&iwloc=&output=embed`;
    maxt.innerText = `${(Number(res.main.temp_max)-273).toFixed(1)}°C`;
    mint.innerText = `${(Number(res.main.temp_min)-273).toFixed(1)}°C`
    hum.innerText = `${res.main.humidity}%`;
    wind.innerText = `${res.wind.speed} km/hr`;
    div3.append(images, paragraph);
    weather.append(div1, div2, div3);
    nextFiveDays(res.name)
    x.style.display="block"
}
let fiveDaysNext = document.querySelector("#next_five_days>div");
function nextFiveDays(city_name) {
    url = `https://api.openweathermap.org/data/2.5/forecast?q=${city_name}&appid=a1e374b0d011589101b5ea37e87c9663`;
    fetch(url).then(function (res) {
        return res.json();
    }).then(function (res) {
        displayNextFiveDays(res.list)
    })
}

function displayNextFiveDays(five_days_data) {
    fiveDaysNext.innerHTML = null;
    let temp = 0;
    let days_arr = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
    for(let i=1;i<6;++i){
        let div=document.createElement("div");
        let day=document.createElement("h3");
        day.innerText=days_arr[i];
        let image=document.createElement("img");
        temp=cel(five_days_data[i].main.temp);
        console.log(temp)
        if(temp<=20){
            image.src=arr1[1];
        }else if(temp>20&&temp<=30){
            image.src=arr1[0];
        }else{
            image.src=arr1[2];
        }
        let min_temp=document.createElement("h3");
        min_temp.innerText=`${cel(five_days_data[i].main.temp_min)}°C`;
        let max_temp=document.createElement("h3");
        max_temp.innerText=`${cel(five_days_data[i].main.temp_max)}°C`;
        div.append(day,image,min_temp,max_temp);
        fiveDaysNext.append(div)
        
    }
}

function cel(num) {
    let res = (Number(num) - 273).toFixed(1);
    return res;
}